<script>
    $(document).ready(function () {
        if ($(window).width() > 1023) {
            var getslide = $('.fan-tabs .fan-box').length - 1;
            var slidecal = 50 / getslide + '%';
            $('.fan-box').css({
                "width": slidecal
            });
            $('.fan-tabs .fan-box:first-child').addClass('show');
            $('.fan-box').click(function () {
                $('.fan-box').removeClass('show');
                $(this).addClass('show');
            });
        }
    });
</script>

<script>

    $(document).ready(function() {
        $(window).resize(function() {
            var width = $(window).width();
            if (width < 768){
                $('#map svg').setAttribute('width','400');
            }
        });
        var atamap = $('.list-country ul li').attr('data-map');
        var texts = $(' main.main .section-home-9 .content .list-sharp--mobile .box .desc');
        var maxHeight = 0;

        texts.each(function() {
            var height = $(this).outerHeight();
            if (height > maxHeight) {
                maxHeight = height;
            }
        });

        var maxHeightRem = maxHeight / parseFloat($('html').css('font-size'));
        console.log(maxHeightRem);
        texts.css('min-height', maxHeightRem + 'rem');
    });
    function addText(p, index, o) {
        var t = document.createElementNS("http://www.w3.org/2000/svg", "text");
        var b = p.getBBox();
        t.setAttribute("transform", "translate(" + (b.x + b.width / 2.5) + " " + (b.y + b.height / 3) + ")");
        t.textContent = index;
        t.setAttribute("fill", "#fff");
        // t.setAttribute("stroke-width", "0.7");
        // t.setAttribute("stroke-linejoin", "bevel");
        t.setAttribute("font-size", "12");
        t.setAttribute("font-weight", "600");
        t.setAttribute("id", $(p).attr('id')  + 'title');
        o.parentNode.insertBefore(t, o.nextSibling);

    }
    var provinces = <?php echo json_encode(getProvinces()); ?>;
    var itemMap = <?= json_encode($main_provinces)?>;

    function initMapBG() {

        let index = 1;
        for (let item of itemMap) {

            $('#' + item.item).css('fill','#FF8C01');
            $('#' +  item.item+'title').css('fill','#fff');
            $('#' + item.item+'title').css("font-size", "10");
            // $('#' + item.item+'title').css('stroke','#000');
            // $('#' + item.item+'title').css("stroke-width", "0.6");
        }
    }
    function initMapBGTitle() {

        let index = 1;
        for (let item of itemMap) {
             $('#' + item.item+'title').css('fill','transparent');
             // $('#' + item.item+'title').css('stroke','transparent');
        }
    }
    initMapBG();

    function initMap() {

        let index = 1;
        for (let item of provinces) {
            let path = document.getElementById(item['item']);
            var title = path.getAttribute('title');
            var id = document.getElementById('VN-55');

            // console.log(title)
            addText(path, title, id);
            index++;
        }
    }
    initMap();

    // Add quan dao text
    function addQDText(p, index) {
        var t = document.createElementNS("http://www.w3.org/2000/svg", "text");
        var b = p.getBBox();
        t.setAttribute("transform", "translate(" + (b.x - 70) + " " + (b.y + b.height / 2) + ")");
        t.textContent = index;
        t.setAttribute("fill", "#2B54A5");
        t.setAttribute("font-size", "10");
        t.setAttribute("font-weight", "700");
        p.parentNode.insertBefore(t, p.nextSibling);
    }

    let qdts = document.getElementById('VN-TS');
    let qdhs = document.getElementById('VN-HS');
    addQDText(qdts, 'QĐ. Trường Sa');
    addQDText(qdhs, 'QĐ. Hoàng Sa');

    $(document).on("mousemove", function (event) {
        // $( "#log" ).text( "pageX: " + event.pageX + ", pageY: " + event.pageY );
        $(".tooltip").css({
            "left": event.pageX,
            "top": event.pageY
        });
    });
    var dataMap = $('.list-country ul li.active').attr("data-map");

    $('.list-country ul li').on("click", function (event) {
        let id_map = $(this).attr("data-map");
        if(id_map ==0) {
            initMapBG();
            // addText();
            return
        }

        $('path').css('fill', '#2B54A5')
        $('#' + id_map).css('fill','#FF8C01');
        initMapBGTitle();
        $('#' + id_map+'title').css('fill','#fff');
        // $('#' + id_map+'title').css('stroke','#000');
        // $('#' + id_map+'title').css("stroke-width", "0.6");
        $('#' + id_map+'title').css("font-size", "14");
        // addText(id_map);
    });
</script>